MOD = 1000000007

def solve():
    n, m = map(int, input().split())

    count = 0

    def check(path):
        edges = []
        curr = 1
        for town in path:
            edges.append((curr, town))
            curr = town

        adj = {i: [] for i in range(1, n + 1)}
        for u, v in edges:
            adj[u].append(v)

        def dfs(start, visited):
            visited.add(start)
            for neighbor in adj[start]:
                if neighbor not in visited:
                    dfs(neighbor, visited)

        visited = set()
        dfs(1, visited)
        return len(visited) == n


    import itertools
    for path_tuple in itertools.product(range(1, n + 1), repeat=m):
        path = list(path_tuple)
        if check(path):
            count +=1

    print(count % MOD)

solve()
