import sys

def solve():
    n, q = map(int, sys.stdin.readline().split())
    edges = []
    for _ in range(q):
        a, b, c = map(int, sys.stdin.readline().split())
        i = 0
        while True:
            na = (a + i) % n
            nb = (b + i) % n
            edges.append((na, nb, c + i))
            i += 1
            if i > 1000:
                break

    parent = list(range(n))
    rank = [0] * n

    def find(i):
        if parent[i] == i:
            return i
        parent[i] = find(parent[i])
        return parent[i]

    def union(i, j):
        root_i = find(i)
        root_j = find(j)
        if root_i != root_j:
            if rank[root_i] < rank[root_j]:
                parent[root_i] = root_j
            elif rank[root_i] > rank[root_j]:
                parent[root_j] = root_i
            else:
                parent[root_j] = root_i
                rank[root_i] += 1
            return True
        return False

    edges.sort(key=lambda x: x[2])
    total_weight = 0
    for u, v, w in edges:
        if union(u, v):
            total_weight += w

    print(total_weight)

solve()
