#include <stdio.h>

int main() {
    int N, M;
    scanf("%d", &N);
    long long A[N];
    for (int i = 0; i < N - 1; i++) {
        scanf("%lld", &A[i]);
    }
    scanf("%d", &M);
    for (int i = 0; i < M; i++) {
        long long X;
        scanf("%lld", &X);
        A[N - 1] = X;
        long long snuke_score = 0;
        long long rng_score = 0;
        int snuke_pos = 0;
        int rng_pos = 1;
        int turn = 0;
        while (snuke_pos < N - 1 && rng_pos < N) {
            if (turn == 0) {
                int best_move = -1;
                long long best_score_diff = -1e18;
                for (int j = snuke_pos + 1; j < N; j++) {
                    if (j != rng_pos) {
                        long long score_diff = A[j] - rng_score;
                        if (score_diff > best_score_diff) {
                            best_score_diff = score_diff;
                            best_move = j;
                        }
                    }
                }
                snuke_score += A[best_move];
                snuke_pos = best_move;
            } else {
                int best_move = -1;
                long long best_score_diff = -1e18;
                for (int j = rng_pos + 1; j < N; j++) {
                    if (j != snuke_pos) {
                        long long score_diff = A[j] - snuke_score;
                        if (score_diff > best_score_diff) {
                            best_score_diff = score_diff;
                            best_move = j;
                        }
                    }
                }
                rng_score += A[best_move];
                rng_pos = best_move;
            }
            turn = 1 - turn;
        }
        printf("%lld\n", snuke_score - rng_score);
    }
    return 0;
}
