#include <iostream>
#include <vector>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<long long> a(n);
    for (int i = 0; i < n - 1; ++i) {
        cin >> a[i];
    }

    int m;
    cin >> m;

    for (int i = 0; i < m; ++i) {
        long long x;
        cin >> x;
        a[n - 1] = x;

        long long snuke_score = 0;
        long long rng_score = 0;
        int snuke_pos = 0;
        int rng_pos = 1;
        vector<bool> snuke_visited(n, false);
        vector<bool> rng_visited(n, false);

        snuke_visited[snuke_pos] = true;
        rng_visited[rng_pos] = true;
        snuke_score += a[snuke_pos];
        rng_score += a[rng_pos];

        while (snuke_pos < n - 1 && rng_pos < n -1) {
            if (snuke_pos < rng_pos) {
                int next_pos = -1;
                for (int j = snuke_pos + 1; j < n; ++j) {
                    if (j != rng_pos) {
                        next_pos = j;
                        break;
                    }
                }
                snuke_pos = next_pos;
                snuke_visited[snuke_pos] = true;
                snuke_score += a[snuke_pos];
            } else {
                int next_pos = -1;
                for (int j = rng_pos + 1; j < n; ++j) {
                    if (j != snuke_pos) {
                        next_pos = j;
                        break;
                    }
                }
                rng_pos = next_pos;
                rng_visited[rng_pos] = true;
                rng_score += a[rng_pos];
            }
        }
        cout << snuke_score - rng_score << endl;
    }

    return 0;
}
