#include <iostream>
#include <vector>
#include <string>
#include <algorithm>
#include <set>

using namespace std;

int main() {
  int h, w;
  cin >> h >> w;

  vector<string> grid(h);
  for (int i = 0; i < h; ++i) {
    cin >> grid[i];
  }

  set<vector<string>> distinct_grids;
  vector<pair<int, int>> ops;

  function<void(vector<string>)> generate = [&](vector<string> current_grid) {
    distinct_grids.insert(current_grid);

    for (int i = 0; i < h; ++i) {
      vector<string> next_grid = current_grid;
      reverse(next_grid[i].begin(), next_grid[i].end());
      generate(next_grid);
    }

    for (int j = 0; j < w; ++j) {
      vector<string> next_grid = current_grid;
      for (int i = 0; i < h; ++i) {
        next_grid[i][j] = current_grid[i][w - 1 - j];
      }
      generate(next_grid);
    }
  };

  generate(grid);
  cout << distinct_grids.size() << endl;
  return 0;
}
