MOD = 1000000007

h, w = map(int, input().split())
grid = [input() for _ in range(h)]

rows = [''.join(sorted(row)) for row in grid]
cols = []
for j in range(w):
    col = ''.join(sorted(rows[i][j] for i in range(h)))
    cols.append(col)

ans = 1
for i in range(h):
    count = {}
    for c in rows[i]:
        count[c] = count.get(c, 0) + 1
    for c in count:
        ans = (ans * (count[c] + 1)) % MOD

for j in range(w):
    count = {}
    for c in cols[j]:
        count[c] = count.get(c, 0) + 1
    for c in count:
      ans = (ans * (count[c] + 1)) % MOD

print(ans)
