import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[][] u = new int[n][1];
        int[][] d = new int[n][1];
        int[][] l = new int[n][1];
        int[][] r = new int[n][1];
        for (int i = 0; i < n; i++) {
            u[i][0] = sc.nextInt();
        }
        for (int i = 0; i < n; i++) {
            d[i][0] = sc.nextInt();
        }
        for (int i = 0; i < n; i++) {
            l[i][0] = sc.nextInt();
        }
        for (int i = 0; i < n; i++) {
            r[i][0] = sc.nextInt();
        }
        sc.close();
        int[][] board = new int[n][n];
        List<String> ans = new ArrayList<>();
        int count = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < l[i][0]; j++) {
                if (board[i][0] == 0) {
                    board[i][0] = 1;
                    ans.add("L" + (i + 1));
                    count++;
                } else {
                    System.out.println("NO");
                    return;
                }
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < u[i][0]; j++) {
                if (board[0][i] == 0) {
                    board[0][i] = 1;
                    ans.add("U" + (i + 1));
                    count++;
                } else {
                    System.out.println("NO");
                    return;
                }
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < r[i][0]; j++) {
                if (board[i][n - 1] == 0) {
                    board[i][n - 1] = 1;
                    ans.add("R" + (i + 1));
                    count++;
                } else {
                    System.out.println("NO");
                    return;
                }
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < d[i][0]; j++) {
                if (board[n - 1][i] == 0) {
                    board[n - 1][i] = 1;
                    ans.add("D" + (i + 1));
                    count++;
                } else {
                    System.out.println("NO");
                    return;
                }
            }
        }
        if (count != n * n) {
            System.out.println("NO");
        } else {
            for (String s : ans) {
                System.out.println(s);
            }
        }
    }
}
