#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int i = 1, sum = 0;
    while (sum < n) {
        sum += i;
        i++;
    }
    if (sum == n) {
        printf("%d\n", i - 1);
    } else {
        int diff = sum - n;
        int j = 1;
        while (diff > 0) {
            if (diff >= j) {
                diff -= j;
                printf("%d\n", j);
            }
            j++;
        }
        for (int k = j; k < i -1; k++) {
            printf("%d\n", k);
        }
    }
    return 0;
}
