#include <iostream>
#include <vector>
#include <numeric>
#include <algorithm>

int main() {
    int n;
    std::cin >> n;
    std::vector<int> solution;
    int sum = 0;
    for (int i = n; i >= 1; --i) {
        if (sum + i <= n) {
            solution.push_back(i);
            sum += i;
        }
    }
    if (sum != n) {
        int diff = n - sum;
        for (int i = 1; i <= n; ++i) {
          if (diff == i) {
            solution.push_back(i);
            break;
          }
        }

        int temp = 0;
        for (int i = 0; i < solution.size(); i++) {
          temp += solution[i];
        }

        while(temp > n){
          solution.pop_back();
          temp = 0;
          for (int i = 0; i < solution.size(); i++) {
            temp += solution[i];
          }
        }

        if (temp < n) {
          int diff2 = n-temp;
          for(int i = 1; i <= n; ++i){
            if(diff2 == i){
              solution.push_back(i);
              break;
            }
          }
        }
    }
    for (int i = 0; i < solution.size(); ++i) {
        std::cout << solution[i] << std::endl;
    }
    return 0;
}
