import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<Integer> ans = new ArrayList<>();
        int sum = 0;
        for (int i = 1; ; i++) {
            if (sum + i <= n) {
                ans.add(i);
                sum += i;
            } else {
                break;
            }
        }
        if (sum < n) {
            int diff = n - sum;
            ans.set(ans.size() - 1, ans.get(ans.size() - 1) + diff);
        }
        for (int x : ans) {
            System.out.println(x);
        }
    }
}
