#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    long long A[N], B[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &A[i], &B[i]);
    }
    long long max_players = 0;
    long long max_score = 0;
    for (int i = 0; i < N; i++) {
        if (B[i] > max_score) {
            max_players += B[i] - max_score;
            max_score = B[i];
        } else {
            max_players += A[i] - 1;
        }
    }

    printf("%lld\n", max_players);
    return 0;
}
