import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int[][] langs = new int[n][];
        for (int i = 0; i < n; i++) {
            int k = sc.nextInt();
            langs[i] = new int[k];
            for (int j = 0; j < k; j++) {
                langs[i][j] = sc.nextInt() - 1;
            }
        }
        boolean[][] adj = new boolean[n][n];
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                boolean canCommunicate = false;
                for (int l1 : langs[i]) {
                    for (int l2 : langs[j]) {
                        if (l1 == l2) {
                            canCommunicate = true;
                            break;
                        }
                    }
                    if (canCommunicate) break;
                }
                adj[i][j] = adj[j][i] = canCommunicate;
            }
        }
        for (int k = 0; k < n; k++) {
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    adj[i][j] = adj[i][j] || (adj[i][k] && adj[k][j]);
                }
            }
        }
        boolean allConnected = true;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                if (!adj[i][j]) {
                    allConnected = false;
                    break;
                }
            }
            if (!allConnected) break;
        }
        System.out.println(allConnected ? "YES" : "NO");
    }
}
