participants = []
for i in range(n):
    line = list(map(int, input().split()))
    participants.append(set(line[1:]))

adj = [[False] * n for _ in range(n)]
for i in range(n):
    for j in range(i + 1, n):
        if participants[i] & participants[j]:
            adj[i][j] = adj[j][i] = True

for k in range(n):
    for i in range(n):
        for j in range(n):
            adj[i][j] = adj[i][j] or (adj[i][k] and adj[k][j])

connected = True
for i in range(n):
    for j in range(i + 1, n):
        if not adj[i][j]:
            connected = False
            break
    if not connected:
        break

if connected:
    print('YES')
else:
    print('NO')

