#include <stdio.h>

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    int count[100001] = {0};
    int cards[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &cards[i]);
        count[cards[i]]++;
    }
    int pairs = 0;
    for (int i = 1; i <= 100000; i++) {
        pairs += count[i] / 2;
        count[i] %= 2;
    }
    int remainder[M];
    for (int i = 0; i < M; i++) {
        remainder[i] = 0;
    }
    for (int i = 0; i < N; i++) {
        remainder[cards[i] % M]++;
    }
    pairs += remainder[0] / 2;
    remainder[0] %= 2;
    for (int i = 1; i <= M / 2; i++) {
        if (i != M - i) {
            pairs += (remainder[i] < remainder[M - i]) ? remainder[i] : remainder[M - i];
        }
    }
    printf("%d\n", pairs);
    return 0;
}
