n, m = map(int, input().split())
x = list(map(int, input().split()))

counts = {}
for val in x:
    counts[val] = counts.get(val, 0) + 1

pairs = 0
remainders = {}
for val in x:
    if counts[val] > 0:
        if counts[val] >= 2:
            pairs += counts[val] // 2
            counts[val] %= 2
        remainder = val % m
        if remainder in remainders and remainders[remainder] > 0:
            pairs += 1
            counts[val] -= 1
            remainders[remainder] -= 1
        else:
            remainders[remainder] = remainders.get(remainder, 0) + counts[val]

print(pairs)

