#include <stdio.h>

long long solve() {
    long long n, a;
    scanf("%lld %lld", &n, &a);
    if (n == 1) return 1;
    long long l = 1, r = 2 * n;
    long long ans = 2 * n;
    while (l <= r) {
        long long mid = l + (r - l) / 2;
        long long cur = 0;
        long long time = 0;
        long long rate = 1;
        if (mid <= a) {
            cur = mid;
        } else {
            long long eat_times = 0;
            long long remain_time = mid;
            long long cookies = 1;
            while(cookies < n){
                long long next_eat_time = time + 1 + a;
                long long next_cookies = cookies + rate * (next_eat_time - time -1);
                if(next_cookies >= n){
                    time = next_eat_time;
                    cookies = n;
                    break;
                }
                time = next_eat_time;
                cookies = next_cookies;
                rate = cookies;
            }
            cur = cookies;
        }

        if (cur >= n) {
            ans = mid;
            r = mid - 1;
        } else {
            l = mid + 1;
        }
    }
    return ans;
}

int main() {
    printf("%lld\n", solve());
    return 0;
}
