#include <iostream>
#include <vector>
#include <map>

using namespace std;

int main() {
  int n;
  cin >> n;

  map<long long, long long> scores;
  for (int i = 0; i < n; ++i) {
    long long a, b;
    cin >> a >> b;
    scores[a] = b;
  }

  long long max_players = 0;
  long long max_score = 0;
  for (auto const& [rank, score] : scores) {
    if (score > max_score) {
      max_players += (score - max_score);
      max_score = score;
    }
    max_players += (rank - 1);

  }


  cout << max_players + scores.rbegin()->first << endl;
  return 0;
}
