import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long a = sc.nextLong();

        if (n <= a + 1) {
            System.out.println(n);
            return;
        }

        long left = 0;
        long right = 2 * n;
        long ans = 2 * n;

        while (left <= right) {
            long mid = left + (right - left) / 2;
            long cookies = 0;
            long time = 0;
            long rate = 1;

            boolean possible = true;

            while (cookies < n) {
                long next_time = (n - cookies + rate -1) / rate;
                long next_cookies = cookies + next_time * rate;

                if (time + next_time + a <= mid) {
                    time += next_time + a;
                    cookies = next_cookies;
                    rate = cookies;
                } else {
                    cookies += (mid - time);
                    time = mid;
                    break;
                }
            }
            if (cookies >= n) {
                ans = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        System.out.println(ans);
    }
}
