#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, m;
  cin >> n >> m;

  long long count = 0;
  long long mod = 1000000007;

  vector<int> c(m);
  vector<vector<bool>> adj(n, vector<bool>(n, false));
  vector<int> current_path;

  function<void(int)> solve = [&](int k) {
    if (k == m) {
      vector<vector<bool>> adj_matrix(n, vector<bool>(n, false));
      int current_node = 0;
      for (int i = 0; i < m; ++i) {
        adj_matrix[current_node][c[i] -1] = true;
        current_node = c[i] - 1;
      }

      vector<vector<bool>> reachable(n, vector<bool>(n, false));
      for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
          if (adj_matrix[i][j]) reachable[i][j] = true;
        }
      }

      for (int k = 0; k < n; ++k) {
          for (int i = 0; i < n; ++i) {
              for (int j = 0; j < n; ++j) {
                  reachable[i][j] = reachable[i][j] || (reachable[i][k] && reachable[k][j]);
              }
          }
      }

      bool connected = true;
      for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
          if (i != j && !reachable[i][j]) {
            connected = false;
            break;
          }
        }
        if (!connected) break;
      }
      if (connected) count = (count + 1) % mod;
      return;
    }

    for (int i = 1; i <= n; ++i) {
      c[k] = i;
      solve(k + 1);
    }
  };

  solve(0);
  cout << count << endl;
  return 0;
}
