MOD = 1000000007

def solve():
    n, m = map(int, input().split())

    ans = 0

    def check(path):
        edges = []
        curr = 1
        for next_node in path:
            edges.append((curr, next_node))
            curr = next_node

        adj = {i: [] for i in range(1, n + 1)}
        for u, v in edges:
            adj[u].append(v)

        def dfs(start, visited, graph):
            visited[start] = True
            for neighbor in graph[start]:
                if not visited[neighbor]:
                    dfs(neighbor, visited, graph)

        for i in range(1, n + 1):
            visited = {j: False for j in range(1, n + 1)}
            dfs(i, visited, adj)
            if not all(visited.values()):
                return False


        rev_adj = {i: [] for i in range(1, n + 1)}
        for u, v in edges:
            rev_adj[v].append(u)

        for i in range(1, n + 1):
            visited = {j: False for j in range(1, n + 1)}
            dfs(i, visited, rev_adj)
            if not all(visited.values()):
                return False

        return True


    import itertools
    for path_tuple in itertools.permutations(range(1,n+1),m):
        path = list(path_tuple)
        if check(path):
            ans +=1
    print(ans % MOD)


solve()
