#include <iostream>
#include <vector>
#include <tuple>
#include <algorithm>

using namespace std;

struct Edge {
    int u, v, w;
};

int get_parent(vector<int>& parent, int i) {
    if (parent[i] == i)
        return i;
    return parent[i] = get_parent(parent, parent[i]);
}

void unite_sets(vector<int>& parent, vector<int>& rank, int a, int b) {
    a = get_parent(parent, a);
    b = get_parent(parent, b);
    if (a != b) {
        if (rank[a] < rank[b])
            swap(a, b);
        parent[b] = a;
        if (rank[a] == rank[b])
            rank[a]++;
    }
}

int main() {
    long long n, q;
    cin >> n >> q;

    vector<tuple<long long, long long, long long>> edges;
    for (int i = 0; i < q; ++i) {
        long long a, b, c;
        cin >> a >> b >> c;
        long long cur_a = a, cur_b = b, cur_c = c;
        for (int j = 0; j < n; ++j) {
            edges.emplace_back(cur_c, cur_a, cur_b);
            cur_a = (cur_a + 1) % n;
            cur_b = (cur_b + 1) % n;
            cur_c++;
        }
    }

    sort(edges.begin(), edges.end());

    vector<int> parent(n);
    vector<int> rank(n, 0);
    for (int i = 0; i < n; ++i)
        parent[i] = i;

    long long mst_weight = 0;
    for (const auto& edge : edges) {
        long long w, u, v;
        tie(w, u, v) = edge;
        if (get_parent(parent, u) != get_parent(parent, v)) {
            mst_weight += w;
            unite_sets(parent, rank, u, v);
        }
    }

    cout << mst_weight << endl;

    return 0;
}
