import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int q = sc.nextInt();
        List<long[]> edges = new ArrayList<>();
        for (int i = 0; i < q; i++) {
            int a = sc.nextInt();
            int b = sc.nextInt();
            long c = sc.nextLong();
            for (int j = 0; j < n; j++) {
                edges.add(new long[]{(a + j) % n, (b + j) % n, c + j});
            }
        }
        Collections.sort(edges, (e1, e2) -> Long.compare(e1[2], e2[2]));
        UnionFind uf = new UnionFind(n);
        long totalWeight = 0;
        for (long[] edge : edges) {
            if (uf.union((int) edge[0], (int) edge[1])) {
                totalWeight += edge[2];
            }
        }
        System.out.println(totalWeight);
    }

    static class UnionFind {
        int[] parent;
        int[] rank;

        UnionFind(int n) {
            parent = new int[n];
            rank = new int[n];
            for (int i = 0; i < n; i++) {
                parent[i] = i;
            }
        }

        int find(int x) {
            if (parent[x] == x) {
                return x;
            }
            return parent[x] = find(parent[x]);
        }

        boolean union(int x, int y) {
            int rootX = find(x);
            int rootY = find(y);
            if (rootX != rootY) {
                if (rank[rootX] < rank[rootY]) {
                    parent[rootX] = rootY;
                } else if (rank[rootX] > rank[rootY]) {
                    parent[rootY] = rootX;
                } else {
                    parent[rootY] = rootX;
                    rank[rootX]++;
                }
                return true;
            }
            return false;
        }
    }
}
