import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] b = new int[n];
        for (int i = 0; i < n; i++) {
            b[i] = sc.nextInt();
        }
        int q = sc.nextInt();
        int[][] ops = new int[q][2];
        for (int i = 0; i < q; i++) {
            ops[i][0] = sc.nextInt() - 1;
            ops[i][1] = sc.nextInt() - 1;
        }
        int minHamming = Integer.MAX_VALUE;
        for (int i = 0; i < (1 << q); i++) {
            int[] a = new int[n];
            for (int j = 0; j < q; j++) {
                if ((i >> j) % 2 == 1) {
                    for (int k = ops[j][0]; k <= ops[j][1]; k++) {
                        a[k] = 1;
                    }
                }
            }
            int hamming = 0;
            for (int j = 0; j < n; j++) {
                if (a[j] != b[j]) {
                    hamming++;
                }
            }
            minHamming = Math.min(minHamming, hamming);
        }
        System.out.println(minHamming);
    }
}
