import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        Map<Long, Long> map = new HashMap<>();
        for (int i = 0; i < n; i++) {
            long a = sc.nextLong();
            long b = sc.nextLong();
            map.put(a, b);
        }
        long max = 0;
        for (Map.Entry<Long, Long> entry : map.entrySet()) {
            max = Math.max(max, entry.getKey() + entry.getValue());
        }
        System.out.println(max);
    }
}
