        vector<bool> rng_visited(n, false);

        while (true) {
            bool snuke_moved = false;
            bool rng_moved = false;

            if (snuke_pos < rng_pos) {
                int best_move = -1;
                long long best_diff = -1e18; 
                for (int j = snuke_pos + 1; j < n; ++j) {
                    if (j != rng_pos) {
                        long long cur_diff = a[j] - (snuke_score + rng_score);
                        if (cur_diff > best_diff) {
                            best_diff = cur_diff;
                            best_move = j;
                        }
                    }
                }
                if (best_move != -1) {
                    snuke_pos = best_move;
                    snuke_score += a[snuke_pos];
                    snuke_moved = true;
                }
            }

