                    rng_pos = best_move;
                    rng_score += a[rng_pos];
                    rng_moved = true;
                }
            }

            if (!snuke_moved && !rng_moved) break;
        }
        cout << snuke_score - rng_score << endl;
    }
    return 0;
}
