    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n - 1; i++) {
            a[i] = sc.nextInt();
        }
        int m = sc.nextInt();
        for (int i = 0; i < m; i++) {
            a[n - 1] = sc.nextInt();
            long snukeScore = 0;
            long rngScore = 0;
            int snukePos = 0;
            int rngPos = 1;
            boolean snukeTurn = true;
            while (snukePos < n - 1 && rngPos < n) {
                if (snukeTurn) {
                    int bestMove = -1;
                    long bestScoreDiff = Long.MIN_VALUE;
                    for (int j = snukePos + 1; j < n; j++) {
                        if (j != rngPos) {
                            long scoreDiff = a[j];
