                                    if (k != j) {
                                        long rngScoreDiff = a[k] - scoreDiff;
                                        if (rngScoreDiff > bestRngScoreDiff) {
                                            bestRngScoreDiff = rngScoreDiff;
                                            bestRngMove = k;
                                        }
                                    }
                                }
                                scoreDiff -= a[bestRngMove];
                            }
                            if (scoreDiff > bestScoreDiff) {
                                bestScoreDiff = scoreDiff;
                                bestMove = j;
                            }
                        }
                    }
                    snukeScore += a[bestMove];
                    snukePos = bestMove;
