                                int bestSnukeMove = -1;
                                long bestSnukeScoreDiff = Long.MIN_VALUE;
                                for (int k = snukePos + 1; k < n; k++) {
                                    if (k != j) {
                                        long snukeScoreDiff = a[k] - scoreDiff;
                                        if (snukeScoreDiff > bestSnukeScoreDiff) {
                                            bestSnukeScoreDiff = snukeScoreDiff;
                                            bestSnukeMove = k;
                                        }
                                    }
                                }
                                scoreDiff -= a[bestSnukeMove];
                            }
                            if (scoreDiff > bestScoreDiff) {
                                bestScoreDiff = scoreDiff;
                                bestMove = j;
                            }
                        }
                    }
                    rngScore += a[bestMove];
                    rngPos = bestMove;
                }
                snukeTurn = !snukeTurn;
            }
            System.out.println(snukeScore - rngScore);
        }
    }
}
