#include <stdio.h>
#include <string.h>

#define MOD 1000000007

int main() {
    int H, W;
    scanf("%d %d", &H, &W);
    char grid[H][W + 1];
    for (int i = 0; i < H; i++) {
        scanf("%s", grid[i]);
    }

    long long count = 0;
    for (int i = 0; i < (1 << (H + W)); i++) {
        char temp_grid[H][W + 1];
        for (int j = 0; j < H; j++) {
            strcpy(temp_grid[j], grid[j]);
        }

        for (int j = 0; j < H; j++) {
            if ((i >> j) & 1) {
                int left = 0, right = W - 1;
                while (left < right) {
                    char temp = temp_grid[j][left];
                    temp_grid[j][left] = temp_grid[j][right];
                    temp_grid[j][right] = temp;
                    left++;
                    right--;
                }
            }
        }

        for (int j = 0; j < W; j++) {
            if ((i >> (j + H)) & 1) {
                int top = 0, bottom = H - 1;
                while (top < bottom) {
                    char temp = temp_grid[top][j];
                    temp_grid[top][j] = temp_grid[bottom][j];
                    temp_grid[bottom][j] = temp;
                    top++;
                    bottom--;
                }
            }
        }

        int unique = 1;
        for (int k = 0; k < i; k++) {
            char other_grid[H][W+1];
            for(int a=0; a<H; ++a) strcpy(other_grid[a], grid[a]);
            for (int j = 0; j < H; j++) {
                if ((k >> j) & 1) {
                    int left = 0, right = W - 1;
                    while (left < right) {
                        char temp = other_grid[j][left];
                        other_grid[j][left] = other_grid[j][right];
                        other_grid[j][right] = temp;
                        left++;
                        right--;
                    }
                }
            }

            for (int j = 0; j < W; j++) {
                if ((k >> (j + H)) & 1) {
                    int top = 0, bottom = H - 1;
                    while (top < bottom) {
                        char temp = other_grid[top][j];
                        other_grid[top][j] = other_grid[bottom][j];
                        other_grid[bottom][j] = temp;
                        top++;
                        bottom--;
                    }
                }
            }
            int same = 1;
            for(int a=0; a<H; ++a){
                if(strcmp(temp_grid[a], other_grid[a]) !=0) same = 0;
            }
            if(same) unique = 0;
        }
        count = (count + unique) % MOD;
    }
    printf("%lld\n", count);
    return 0;
}
