MOD = 1000000007

def solve():
    h, w = map(int, input().split())
    grid = [input() for _ in range(h)]

    rows = ["".join(sorted(row)) for row in grid]
    cols = ["".join(sorted(grid[i][j] for i in range(h))) for j in range(w)]

    row_count = {}
    for row in rows:
        row_count[row] = row_count.get(row, 0) + 1

    col_count = {}
    for col in cols:
        col_count[col] = col_count.get(col, 0) + 1

    ans = 1
    for count in row_count.values():
        ans = (ans * count) % MOD
    for count in col_count.values():
        ans = (ans * count) % MOD

    print(ans)

solve()
