#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    int U[N], D[N], L[N], R[N];
    for (int i = 0; i < N; i++) scanf("%d", &U[i]);
    for (int i = 0; i < N; i++) scanf("%d", &D[i]);
    for (int i = 0; i < N; i++) scanf("%d", &L[i]);
    for (int i = 0; i < N; i++) scanf("%d", &R[i]);
    int board[N][N];
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            board[i][j] = 0;
        }
    }
    int count = 0;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < L[i]; j++) {
            printf("L%d\n", i + 1);
            int x = i, y = 0;
            while (x < N && y < N && board[x][y] > 0) {
                x++;
                y++;
            }
            if (x >= N || y >= N) {
                printf("NO\n");
                return 0;
            }
            board[x][y]++;
            count++;

        }
    }
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < R[i]; j++) {
            printf("R%d\n", i + 1);
            int x = i, y = N - 1;
            while (x < N && y >= 0 && board[x][y] > 0) {
                x++;
                y--;
            }
            if (x >= N || y < 0) {
                printf("NO\n");
                return 0;
            }
            board[x][y]++;
            count++;
        }
    }
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < U[i]; j++) {
            printf("U%d\n", i + 1);
            int x = 0, y = i;
            while (x < N && y < N && board[x][y] > 0) {
                x++;
                y++;
            }
            if (x >= N || y >= N) {
                printf("NO\n");
                return 0;
            }
            board[x][y]++;
            count++;
        }
    }
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < D[i]; j++) {
            printf("D%d\n", i + 1);
            int x = N - 1, y = i;
            while (x >= 0 && y < N && board[x][y] > 0) {
                x--;
                y++;
            }
            if (x < 0 || y >= N) {
                printf("NO\n");
                return 0;
            }
            board[x][y]++;
            count++;
        }
    }
    if (count != N * N) printf("NO\n");
    return 0;
}
