n = int(input())
u = list(map(int, input().split()))
d = list(map(int, input().split()))
l = list(map(int, input().split()))
r = list(map(int, input().split()))

board = [[0] * n for _ in range(n)]
ans = []
sockets = {
    'U': [(0, i) for i in range(n)],
    'D': [(n - 1, i) for i in range(n)],
    'L': [(i, 0) for i in range(n)],
    'R': [(i, n - 1) for i in range(n)],
}
counts = {'U': u, 'D': d, 'L': l, 'R': r}
directions = {'U': (0, 1), 'D': (0, -1), 'L': (1, 0), 'R': (-1, 0)}


def insert(x, y, direction):
    dx, dy = directions[direction]
    cx, cy = x, y
    while 0 <= cx < n and 0 <= cy < n:
        if board[cx][cy] == 0:
            board[cx][cy] = 1
            return
        cx += dx
        cy += dy
    return False

for direction in ['U', 'D', 'L', 'R']:
    for i in range(n):
        for _ in range(counts[direction][i]):
            x, y = sockets[direction][i]
            if insert(x,y,direction):
                ans.append(direction + str(i + 1))
            else:
                print("NO")
                exit()


if sum(sum(row) for row in board) == n * n:
    for item in ans:
        print(item)
else:
    print("NO")

