#include <iostream>

using namespace std;

int main() {
  long long n, k;
  cin >> n >> k;

  long long count = 0;
  for (long long i = 1; i <= n - 2; ++i) {
    for (long long j = 1; j <= 4; ++j) {
      long long sum = 0;
      for (long long row = i; row < i + 3; ++row) {
        for (long long col = j; col < j + 3; ++col) {
          sum += 7 * row + col - 7;
        }
      }
      if (sum % 11 == k) {
        count++;
      }
    }
  }
  cout << count << endl;
  return 0;
}
