import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long k = sc.nextLong();
        long count = 0;
        for (long i = 1; i <= n - 2; i++) {
            for (long j = 1; j <= 5; j++) {
                long sum = 0;
                for (long x = 0; x < 3; x++) {
                    for (long y = 0; y < 3; y++) {
                        sum += 7 * (i + x) + j + y - 7;
                    }
                }
                if (sum % 11 == k) {
                    count++;
                }
            }
        }
        System.out.println(count);
        sc.close();
    }
}
