n, k = map(int, input().split())

count = 0
for i in range(1, n - 2 + 1):
    for j in range(1, 7 - 2 + 1):
        total = 0
        for x in range(3):
            for y in range(3):
                total += 7 * (i + x -1) + (j + y -1)
        if total % 11 == k:
            count += 1

print(count)
