#include <iostream>
#include <vector>

using namespace std;

int main() {
  int h, w;
  cin >> h >> w;

  vector<vector<int>> board(h, vector<int>(w));
  for (int i = 0; i < h; ++i) {
    for (int j = 0; j < w; ++j) {
      cin >> board[i][j];
    }
  }

  long long max_score = -1;

  for (int r = 0; r < h; ++r) {
    for (int c = 0; c < w; ++c) {
      vector<vector<int>> temp_board = board;
      temp_board[r][c] = 0;

      long long current_score = 0;
      long long chain_count = 0;

      while (true) {
        bool disappeared = false;
        vector<vector<bool>> to_remove(h, vector<bool>(w, false));

        for (int i = 0; i < h; ++i) {
          int count = 1;
          for (int j = 1; j < w; ++j) {
            if (temp_board[i][j] == temp_board[i][j - 1] && temp_board[i][j] != 0) {
              count++;
            } else {
              if (count >= 3) {
                for (int k = 0; k < count; ++k) {
                  to_remove[i][j - k] = true;
                }
              }
              count = 1;
            }
          }
          if (count >= 3) {
            for (int k = 0; k < count; ++k) {
              to_remove[i][w - 1 - k] = true;
            }
          }
        }

        for (int j = 0; j < w; ++j) {
          int count = 1;
          for (int i = 1; i < h; ++i) {
            if (temp_board[i][j] == temp_board[i - 1][j] && temp_board[i][j] != 0) {
              count++;
            } else {
              if (count >= 3) {
                for (int k = 0; k < count; ++k) {
                  to_remove[i - k][j] = true;
                }
              }
              count = 1;
            }
          }
          if (count >= 3) {
            for (int k = 0; k < count; ++k) {
              to_remove[h - 1 - k][j] = true;
            }
          }
        }

        long long chain_sum = 0;
        for (int i = 0; i < h; ++i) {
          for (int j = 0; j < w; ++j) {
            if (to_remove[i][j]) {
              chain_sum += temp_board[i][j];
              disappeared = true;
            }
          }
        }
        if (disappeared) {
          current_score += (1LL << chain_count) * chain_sum;
          chain_count++;
          vector<vector<int>> next_board(h, vector<int>(w,0));
          for (int i = 0; i < h; ++i) {
            for (int j = 0; j < w; ++j) {
              if (!to_remove[i][j]) next_board[i][j] = temp_board[i][j];
            }
          }

          for (int j = 0; j < w; ++j) {
              int cur = h-1;
              for (int i = h-1; i >=0; --i) {
                  if (next_board[i][j] != 0) {
                      next_board[cur][j] = next_board[i][j];
                      cur--;
                  }
              }
          }
          temp_board = next_board;

        } else {
          break;
        }
      }
      max_score = max(max_score, current_score);
    }
  }
  cout << max_score << endl;
  return 0;
}
