#include <stdio.h>

#define MOD 1000000007

int main() {
    int n, k;
    scanf("%d %d", &n, &k);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    long long count = 0;
    for (int i = 0; i < (1 << n); i++) {
        int xor_sum = 0;
        int len = 0;
        for (int j = 0; j < n; j++) {
            if ((i >> j) & 1) {
                xor_sum ^= a[j];
                len++;
            }
        }
        if (xor_sum == k) {
            long long ways = 1;
            for (int j = 1; j <= len; j++) {
                ways = (ways * j) % MOD;
            }
            count = (count + ways) % MOD;

        }
    }
    printf("%lld\n", count);
    return 0;
}
