#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, k;
  cin >> n >> k;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long count = 0;
  for (int i = 0; i < (1 << n); ++i) {
    vector<long long> b;
    long long xor_sum = 0;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        b.push_back(a[j]);
        xor_sum ^= a[j];
      }
    }
    if (xor_sum == k) {
      count++;
    }
  }
  cout << count << endl;
  return 0;
}
