import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        long count = 0;
        for (int i = 0; i < (1 << n); i++) {
            List<Integer> b = new ArrayList<>();
            int xorSum = 0;
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    b.add(a[j]);
                    xorSum ^= a[j];
                }
            }
            if (xorSum == k) {
                count++;
            }
        }
        long ans = 0;
        for (int i = 0; i < (1 << n); i++) {
            List<Integer> subset = new ArrayList<>();
            int xorSum = 0;
            for (int j = 0; j < n; j++) {
                if ((i >> j) & 1) {
                    subset.add(a[j]);
                    xorSum ^= a[j];
                }
            }
            if (xorSum == k) {
                long factorial = 1;
                for (int j = 1; j <= subset.size(); j++) {
                    factorial = (factorial * j) % 1000000007;
                }
                ans = (ans + factorial) % 1000000007;
            }
        }
        System.out.println(ans);
    }
}
