import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int H = sc.nextInt();
        int W = sc.nextInt();
        int[][] a = new int[H][W];
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                a[i][j] = sc.nextInt();
            }
        }
        long maxSouvenirs = 0;
        for (int i = 0; i < (1 << (H + W - 2)); i++) {
            long currentSouvenirs = 0;
            int x1 = 0, y1 = 0;
            int x2 = 0, y2 = 0;
            boolean possible = true;
            for (int j = 0; j < H + W - 2; j++) {
                int move = (i >> j) & 1;
                if (x1 == H -1 && y1 == W -1) {
                    if(x2 == H -1 && y2 == W -1) break;
                    if (move == 0) {
                        if (x2 + 1 < H) x2++;
                        else if (y2 + 1 < W) y2++;
                        else possible = false;
                    } else {
                        if (y2 + 1 < W) y2++;
                        else if (x2 + 1 < H) x2++;
                        else possible = false;
                    }

                } else if (x2 == H -1 && y2 == W -1){
                    if (move == 0) {
                        if (x1 + 1 < H) x1++;
                        else if (y1 + 1 < W) y1++;
                        else possible = false;
                    } else {
                        if (y1 + 1 < W) y1++;
                        else if (x1 + 1 < H) x1++;
                        else possible = false;
                    }
                } else {
                    if (move == 0) {
                        if (x1 + 1 < H) x1++;
                        else if (y1 + 1 < W) y1++;
                        else possible = false;
                    } else {
                        if (y1 + 1 < W) y1++;
                        else if (x1 + 1 < H) x1++;
                        else possible = false;
                    }
                    if (move == 0) {
                        if (x2 + 1 < H) x2++;
                        else if (y2 + 1 < W) y2++;
                        else possible = false;
                    } else {
                        if (y2 + 1 < W) y2++;
                        else if (x2 + 1 < H) x2++;
                        else possible = false;
                    }
                }
                if (!possible) break;

            }
            if (possible && x1 == H - 1 && y1 == W - 1 && x2 == H -1 && y2 == W -1) {
                currentSouvenirs = a[0][0];
                int curX = 0;
                int curY = 0;
                for (int j = 0; j < H + W -2; j++){
                    int move = (i >> j) & 1;
                    if (move == 0) curX++;
                    else curY++;
                    currentSouvenirs += a[curX][curY];
                }
                maxSouvenirs = Math.max(maxSouvenirs, currentSouvenirs);
            }
        }
        System.out.println(maxSouvenirs);
    }
}
