h, w = map(int, input().split())
a = []
for i in range(h):
    a.append(list(map(int, input().split())))

dp = {}
def solve(r1, c1, r2, c2, steps):
    if (r1, c1, r2, c2, steps) in dp:
        return dp[(r1, c1, r2, c2, steps)]
    if steps == h + w - 2:
        if r1 == h - 1 and c1 == w - 1 and r2 == h -1 and c2 == w -1:
            return a[r1][c1] + a[r2][c2] if r1 != r2 or c1 != c2 else a[r1][c1]
        else:
            return -1
    ans = -1
    for dr1, dc1 in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        nr1, nc1 = r1 + dr1, c1 + dc1
        if 0 <= nr1 < h and 0 <= nc1 < w:
            for dr2, dc2 in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nr2, nc2 = r2 + dr2, c2 + dc2
                if 0 <= nr2 < h and 0 <= nc2 < w:
                    cur = solve(nr1, nc1, nr2, nc2, steps + 1)
                    if cur != -1:
                        ans = max(ans, cur + (a[r1][c1] if r1 != r2 or c1 != c2 else 0) + (a[r2][c2] if r1 != r2 or c1 != c2 else 0) )

    dp[(r1, c1, r2, c2, steps)] = ans
    return ans

print(solve(0, 0, 0, 0, 0))

