    long long n, k;
    scanf("%lld %lld", &n, &k);
    double *areas = (double *)malloc(sizeof(double) * (n * (n - 1) * (n - 2) / 6));
    long long count = 0;
    for (long long i = 0; i < n; i++) {
        for (long long j = i + 1; j < n; j++) {
            for (long long l = j + 1; l < n; l++) {
                double a = 2 * sin(M_PI * (j - i) / n);
                double b = 2 * sin(M_PI * (l - j) / n);
                double c = 2 * sin(M_PI * (i - l + n) / n);
                double s = (a + b + c) / 2;
                double area = sqrt(s * (s - a) * (s - b) * (s - c)) / 2;
                areas[count++] = area;
            }
        }
    }
    qsort(areas, count, sizeof(double), [](const void *a, const void *b) {
        return *(double *)a > *(double *)b ? 1 : -1;
    });
    printf("%.12lf\n", areas[k - 1]);
    free(areas);
    return 0;
}

