#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>
#include <iomanip>

using namespace std;

int main() {
    long long n, k;
    cin >> n >> k;

    vector<double> areas;
    for (long long i = 0; i < n; ++i) {
        for (long long j = i + 1; j < n; ++j) {
            for (long long l = j + 1; l < n; ++l) {
                double a = 2 * sin(M_PI * (j - i) / n);
                double b = 2 * sin(M_PI * (l - j) / n);
                double c = 2 * sin(M_PI * (i - l + n) / n);
                double s = (a + b + c) / 2;
                double area = sqrt(s * (s - a) * (s - b) * (s - c));
                areas.push_back(area / 4);
            }
        }
    }

    sort(areas.begin(), areas.end());

    cout << fixed << setprecision(15) << areas[k - 1] << endl;

    return 0;
}
