import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        double[] areas = new double[(n * (n - 1) * (n - 2)) / 6];
        int index = 0;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                for (int l = j + 1; l < n; l++) {
                    double a = Math.abs(i - j);
                    double b = Math.abs(j - l);
                    double c = Math.abs(l - i);
                    double s = (a + b + c) / 2;
                    double area = Math.sqrt(s * (s - a) * (s - b) * (s - c));
                    areas[index++] = area * Math.sin(Math.PI / n);


                }
            }
        }
        Arrays.sort(areas);
        System.out.printf("%.12f\n", areas[k - 1] );
    }
}
