#include <stdio.h>
#include <stdlib.h>

int main() {
    long long N, Q;
    scanf("%lld %lld", &N, &Q);
    long long *dishes = (long long *)malloc(N * sizeof(long long));
    for (long long i = 0; i < N; i++) {
        dishes[i] = 0;
    }
    for (long long i = 0; i < Q; i++) {
        long long a, b;
        scanf("%lld %lld", &a, &b);
        for (long long j = 0; j < b; j++) {
            long long min_dishes = -1;
            long long min_customer = -1;
            for (long long k = 0; k < a; k++) {
                if (min_customer == -1 || dishes[k] < min_dishes || (dishes[k] == min_dishes && k < min_customer)) {
                    min_dishes = dishes[k];
                    min_customer = k;
                }
            }
            dishes[min_customer]++;
        }
    }
    for (long long i = 0; i < N; i++) {
        printf("%lld\n", dishes[i]);
    }
    free(dishes);
    return 0;
}
