#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, q;
  cin >> n >> q;

  vector<long long> a(q), b(q);
  for (int i = 0; i < q; ++i) {
    cin >> a[i] >> b[i];
  }

  vector<long long> dishes(n, 0);
  for (int i = 0; i < q; ++i) {
    for (long long j = 0; j < b[i]; ++j) {
      long long min_dishes = -1;
      long long min_customer = -1;
      for (long long k = 0; k < a[i]; ++k) {
        if (min_customer == -1 || dishes[k] < min_dishes || (dishes[k] == min_dishes && k < min_customer)) {
          min_dishes = dishes[k];
          min_customer = k;
        }
      }
      dishes[min_customer]++;
    }
  }

  for (int i = 0; i < n; ++i) {
    cout << dishes[i] << endl;
  }

  return 0;
}
