import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long q = sc.nextLong();
        long[] a = new long[(int) q];
        long[] b = new long[(int) q];
        for (int i = 0; i < q; i++) {
            a[i] = sc.nextLong();
            b[i] = sc.nextLong();
        }
        long[] c = new long[(int) n];
        for (int i = 0; i < q; i++) {
            long min = Long.MAX_VALUE;
            long minIndex = -1;
            for (int j = 0; j < a[i]; j++) {
                if (c[j] < min) {
                    min = c[j];
                    minIndex = j;
                }
            }
            long add = Math.min(b[i],Long.MAX_VALUE - c[(int) minIndex]);
            c[(int) minIndex] += add;
            b[i] -= add;
        }
        for (int i = 0; i < n; i++) {
            System.out.println(c[i]);
        }
    }
}
