n, q = map(int, input().split())
customers = [0] * n
for _ in range(q):
    a, b = map(int, input().split())
    min_dishes = float('inf')
    min_customer = -1
    for i in range(a):
        if customers[i] < min_dishes:
            min_dishes = customers[i]
            min_customer = i

    add = min(b, (customers[0] if 0 < a else 0) - customers[min_customer] + 1)

    for i in range(a):
        if customers[i] == min_dishes:
            customers[i] += add

for c in customers:
    print(c)

