#include <stdio.h>

#define MOD 998244353

int main() {
    long long n, m;
    scanf("%lld %lld", &n, &m);

    long long a[200001];
    a[1] = a[2] = 1;
    for (long long i = 3; i <= m; i++) {
        a[i] = (a[i - 1] + a[i - 2]) % MOD;
    }

    long long d[200001][200001];
    for (long long i = 1; i <= m; i++) {
        d[1][i] = a[i];
    }

    for (long long i = 2; i <= n; i++) {
        for (long long j = 1; j <= m; j++) {
            d[i][j] = 0;
            for (long long k = 1; k <= j; k++) {
                d[i][j] = (d[i][j] + d[i - 1][k]) % MOD;
            }
        }
    }

    printf("%lld\n", d[n][m]);

    return 0;
}
